import React, { useState } from 'react';
import axios from 'axios';
import { FileText, Lock, Upload, AlertCircle, CheckCircle, Loader } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isDragOver, setIsDragOver] = useState(false);
  const [activeTab, setActiveTab] = useState('wordToPdf');

  const resetStates = () => {
    setFile(null);
    setPassword('');
    setError('');
    setSuccess('');
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    resetStates();
  };

  const validateFile = (fileToValidate) => {
    if (!fileToValidate) return false;

    if (activeTab === 'wordToPdf') {
      return fileToValidate.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document';
    }
    return fileToValidate.type === 'application/pdf';
  };

  const handleFileChange = (fileInput) => {
    if (!fileInput) {
      setError('Please select a file.');
      setFile(null);
      return;
    }

    if (!validateFile(fileInput)) {
      setError(
        activeTab === 'wordToPdf'
          ? 'Please select a valid Word (.docx) file.'
          : 'Please select a valid PDF file.'
      );
      setFile(null);
      return;
    }

    setFile(fileInput);
    setError('');
    setSuccess('');
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange(droppedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a file first.');
      return;
    }

    if (activeTab === 'pdfProtect' && !password) {
      setError('Please enter a password for the PDF.');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    const formData = new FormData();
    formData.append('file', file);
    if (activeTab === 'pdfProtect') {
      formData.append('password', password);
    }

    try {
      const endpoint = activeTab === 'wordToPdf' 
        ? 'http://localhost:5000/convert' 
        : 'http://localhost:5001/upload';

      const response = await axios.post(endpoint, formData, {
        responseType: 'blob',
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      const fileName = activeTab === 'wordToPdf'
        ? `${file.name.replace('.docx', '')}_converted.pdf`
        : `${file.name.replace('.pdf', '')}_protected.pdf`;

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setSuccess(
        activeTab === 'wordToPdf'
          ? 'File successfully converted! Download started.'
          : 'PDF successfully protected! Download started.'
      );
      setFile(null);
      setPassword('');
    } catch (err) {
      console.error('Error:', err);
      setError(
        activeTab === 'wordToPdf'
          ? 'Error converting file. Please try again.'
          : 'Error protecting PDF. Please try again.'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl p-8">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          File Converter & Protector
        </h1>

        {/* Tabs */}
        <div className="flex justify-center space-x-4 mb-8">
          <button
            onClick={() => handleTabChange('wordToPdf')}
            className={`flex items-center space-x-2 px-6 py-3 rounded-lg transition-all ${
              activeTab === 'wordToPdf'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            <FileText size={20} />
            <span>Word to PDF</span>
          </button>
          <button
            onClick={() => handleTabChange('pdfProtect')}
            className={`flex items-center space-x-2 px-6 py-3 rounded-lg transition-all ${
              activeTab === 'pdfProtect'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            <Lock size={20} />
            <span>Protect PDF</span>
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* File Upload Area */}
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`relative border-2 border-dashed rounded-xl p-8 text-center transition-all ${
              isDragOver
                ? 'border-blue-400 bg-blue-50'
                : 'border-gray-300 bg-gray-50 hover:bg-gray-100'
            }`}
          >
            <input
              type="file"
              id="fileInput"
              accept={activeTab === 'wordToPdf' ? '.docx' : '.pdf'}
              onChange={(e) => handleFileChange(e.target.files[0])}
              className="hidden"
            />
            <label htmlFor="fileInput" className="cursor-pointer">
              <div className="flex flex-col items-center gap-4">
                <Upload size={40} className="text-gray-400" />
                {file ? (
                  <div className="flex items-center gap-2 text-blue-600">
                    <CheckCircle size={20} />
                    <span className="font-medium">{file.name}</span>
                  </div>
                ) : (
                  <>
                    <p className="text-lg font-medium text-gray-600">
                      Drag and drop your file here or click to browse
                    </p>
                    <p className="text-sm text-gray-500">
                      {activeTab === 'wordToPdf' ? 'Accepts .docx files' : 'Accepts PDF files'}
                    </p>
                  </>
                )}
              </div>
            </label>
          </div>

          {/* Password Input for PDF Protection */}
          {activeTab === 'pdfProtect' && (
            <input
              type="password"
              placeholder="Enter password to protect PDF"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all"
            />
          )}

          {/* Error Message */}
          {error && (
            <Alert variant="destructive">
              <AlertCircle className="h-4 w-4" />
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {/* Success Message */}
          {success && (
            <Alert className="bg-green-50 text-green-700 border-green-200">
              <CheckCircle className="h-4 w-4" />
              <AlertDescription>{success}</AlertDescription>
            </Alert>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 px-4 rounded-lg text-white font-medium flex items-center justify-center space-x-2 ${
              loading
                ? 'bg-blue-400 cursor-not-allowed'
                : 'bg-blue-600 hover:bg-blue-700'
            } transition-colors shadow-md`}
          >
            {loading ? (
              <>
                <Loader className="animate-spin" size={20} />
                <span>{activeTab === 'wordToPdf' ? 'Converting...' : 'Protecting...'}</span>
              </>
            ) : (
              <>
                {activeTab === 'wordToPdf' ? (
                  <>
                    <FileText size={20} />
                    <span>Convert to PDF</span>
                  </>
                ) : (
                  <>
                    <Lock size={20} />
                    <span>Protect PDF</span>
                  </>
                )}
              </>
            )}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FileConverter;
