import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [metadata, setMetadata] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const [activeTab, setActiveTab] = useState('wordToPdf'); // Toggle between functionalities

  const handleFileChange = (fileInput) => {
    setFile(fileInput);
    setError('');
    setMetadata(null);
  };

  const validateFile = () => {
    if (activeTab === 'wordToPdf') {
      return (
        file &&
        file.type ===
          'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
      );
    }
    return file && file.type === 'application/pdf';
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFileChange(selectedFile);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange(droppedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateFile()) {
      setError(
        activeTab === 'wordToPdf'
          ? 'Please select a valid .docx file.'
          : 'Please select a valid PDF file.'
      );
      return;
    }

    if (activeTab === 'pdfProtect' && !password) {
      setError('Please enter a password for the PDF.');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);
    if (activeTab === 'pdfProtect') {
      formData.append('password', password);
    }

    try {
      const endpoint =
        activeTab === 'wordToPdf' ? 'http://localhost:5000/convert' : 'http://localhost:5002/upload';
      const response = await axios.post(endpoint, formData, { responseType: 'blob' });

      const fileName =
        activeTab === 'wordToPdf'
          ? 'converted.pdf'
          : `protected_${file.name.replace('.pdf', '')}.pdf`;

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
      setPassword('');
      setMetadata(null);
    } catch (err) {
      setError(
        activeTab === 'wordToPdf'
          ? 'Error converting file. Please try again.'
          : 'Error protecting PDF. Please try again.'
      );
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-6 bg-gradient-to-b from-blue-100 to-gray-200">
      <div className="w-full md:w-2/3 max-w-2xl p-6 bg-white rounded-lg shadow-lg">
        <h1 className="text-3xl font-bold text-gray-800 text-center mb-4">
          File Conversion & Protection
        </h1>
        <div className="flex justify-center space-x-4 mb-6">
          <button
            onClick={() => setActiveTab('wordToPdf')}
            className={`px-4 py-2 rounded ${
              activeTab === 'wordToPdf'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
            }`}
          >
            Word to PDF
          </button>
          <button
            onClick={() => setActiveTab('pdfProtect')}
            className={`px-4 py-2 rounded ${
              activeTab === 'pdfProtect'
                ? 'bg-blue-600 text-white'
                : 'bg-gray-200 text-gray-600 hover:bg-gray-300'
            }`}
          >
            Protect PDF
          </button>
        </div>

        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`block w-full p-6 text-center border-2 border-dashed rounded-lg cursor-pointer focus:outline-none ${
              isDragOver ? 'bg-blue-100 border-blue-400' : 'bg-gray-50 border-gray-300'
            }`}
          >
            {file ? (
              <p className="text-gray-800">{file.name}</p>
            ) : (
              <p className="text-gray-500">Drag and drop your file here</p>
            )}
          </div>
          <p className="text-center text-gray-500">or</p>
          <label
            htmlFor="fileInput"
            className="block w-full p-3 text-center text-gray-700 bg-gray-200 rounded-lg cursor-pointer hover:bg-gray-300 focus:outline-none"
          >
            Select File
          </label>
          <input
            id="fileInput"
            type="file"
            accept={activeTab === 'wordToPdf' ? '.docx' : '.pdf'}
            className="hidden"
            onChange={handleFileInput}
          />

          {activeTab === 'pdfProtect' && (
            <input
              type="password"
              placeholder="Enter password for PDF"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="p-3 w-full border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          )}

          {error && <p className="text-sm text-red-500 text-center">{error}</p>}

          <button
            type="submit"
            disabled={loading}
            className={`w-full p-3 ${
              loading ? 'bg-blue-400' : 'bg-blue-600 hover:bg-blue-700'
            } text-white rounded-lg shadow-md focus:outline-none ${
              loading && 'opacity-50 cursor-not-allowed'
            }`}
          >
            {loading
              ? activeTab === 'wordToPdf'
                ? 'Converting...'
                : 'Encrypting...'
              : activeTab === 'wordToPdf'
              ? 'Convert to PDF'
              : 'Download Encrypted PDF'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FileConverter;
