import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isDragOver, setIsDragOver] = useState(false);
  const [activeTab, setActiveTab] = useState('wordToPdf');

  const resetStates = () => {
    setFile(null);
    setPassword('');
    setError('');
    setSuccess('');
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    resetStates();
  };

  const validateFile = (fileToValidate) => {
    if (!fileToValidate) return false;

    if (activeTab === 'wordToPdf') {
      return fileToValidate.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document';
    }
    return fileToValidate.type === 'application/pdf';
  };

  const handleFileChange = (fileInput) => {
    if (!fileInput) {
      setError('Please select a file.');
      setFile(null);
      return;
    }

    if (!validateFile(fileInput)) {
      setError(
        activeTab === 'wordToPdf'
          ? 'Please select a valid Word (.docx) file.'
          : 'Please select a valid PDF file.'
      );
      setFile(null);
      return;
    }

    setFile(fileInput);
    setError('');
    setSuccess('');
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange(droppedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a file first.');
      return;
    }

    if (activeTab === 'pdfProtect' && !password) {
      setError('Please enter a password for the PDF.');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    const formData = new FormData();
    if (activeTab === 'wordToPdf') {
      formData.append('file', file);
    } else {
      formData.append('pdfFile', file);
      formData.append('password', password);
    }

    try {
      const endpoint =
        activeTab === 'wordToPdf'
          ? 'http://localhost:5000/convert'
          : 'http://localhost:5001/upload';

      const response = await axios.post(endpoint, formData, {
        responseType: 'blob',
        headers: {
          Accept: 'application/pdf',
        },
        validateStatus: function (status) {
          return status < 500;
        },
      });

      if (response.status === 400) {
        const errorText = await new Response(response.data).text();
        throw new Error(errorText || 'Bad request - please check your input');
      }

      const fileName =
        activeTab === 'wordToPdf'
          ? `${file.name.replace('.docx', '')}_converted.pdf`
          : `${file.name.replace('.pdf', '')}_protected.pdf`;

      const url = window.URL.createObjectURL(
        new Blob([response.data], { type: 'application/pdf' })
      );
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', fileName);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setSuccess(
        activeTab === 'wordToPdf'
          ? 'File successfully converted! Download started.'
          : 'PDF successfully protected! Download started.'
      );
      setFile(null);
      setPassword('');
    } catch (err) {
      console.error('Error details:', err);

      let errorMessage = 'An unexpected error occurred.';
      if (err.response) {
        if (err.response.status === 400) {
          errorMessage = 'Invalid request. Please check your file and password.';
        } else if (err.response.status === 413) {
          errorMessage = 'File is too large. Please try a smaller file.';
        }
      } else if (err.request) {
        errorMessage = 'No response from server. Please try again later.';
      } else {
        errorMessage = err.message;
      }

      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="w-full max-w-2xl bg-white rounded-2xl shadow-xl p-8">
        <h1 className="text-3xl font-bold text-center text-gray-800 mb-8">
          File Converter & Protector
        </h1>

        {/* Tabs */}
        <div className="flex justify-center space-x-4 mb-8">
          <button
            type="button"
            onClick={() => handleTabChange('wordToPdf')}
            className={`px-6 py-3 rounded-lg transition-all ${
              activeTab === 'wordToPdf'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Word to PDF
          </button>
          <button
            type="button"
            onClick={() => handleTabChange('pdfProtect')}
            className={`px-6 py-3 rounded-lg transition-all ${
              activeTab === 'pdfProtect'
                ? 'bg-blue-600 text-white shadow-md'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            Protect PDF
          </button>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* File Upload Area */}
          <div className="flex items-start gap-6">
            <div
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
              className={`relative border-2 border-dashed rounded-xl p-8 text-center transition-all flex-grow ${
                isDragOver
                  ? 'border-blue-400 bg-blue-50'
                  : 'border-gray-300 bg-gray-50 hover:bg-gray-100'
              }`}
            >
              <input
                type="file"
                id="fileInput"
                accept={activeTab === 'wordToPdf' ? '.docx' : '.pdf'}
                onChange={(e) => handleFileChange(e.target.files[0])}
                className="hidden"
              />
              <label htmlFor="fileInput" className="cursor-pointer">
                <div className="flex flex-col items-center gap-4">
                  {file ? (
                    <div className="text-blue-600 font-medium">{file.name}</div>
                  ) : (
                    <>
                      <p className="text-lg font-medium text-gray-600">
                        Drag and drop your file here or click to browse
                      </p>
                      <p className="text-sm text-gray-500">
                        {activeTab === 'wordToPdf'
                          ? 'Accepts .docx files'
                          : 'Accepts PDF files'}
                      </p>
                    </>
                  )}
                </div>
              </label>
            </div>

            {/* Metadata Section */}
            {file && activeTab === 'wordToPdf' && (
              <div className="w-1/3 bg-gray-100 p-4 rounded-lg shadow-md">
                <h3 className="text-lg font-bold text-gray-800 mb-2">
                  File Metadata
                </h3>
                <p className="text-sm text-gray-600">
                  <strong>Name:</strong> {file.name}
                </p>
                <p className="text-sm text-gray-600">
                  <strong>Type:</strong> {file.type}
                </p>
                <p className="text-sm text-gray-600">
                  <strong>Size:</strong> {(file.size / 1024).toFixed(2)} KB
                </p>
              </div>
            )}
          </div>

          {/* Password Input for PDF Protection */}
          {activeTab === 'pdfProtect' && (
            <div className="space-y-2">
              <input
                type="password"
                placeholder="Enter password to protect PDF"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent outline-none transition-all"
                minLength="4"
                required
              />
              <p className="text-sm text-gray-500">
                Password must be at least 4 characters long
              </p>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="text-red-600 font-medium text-center">{error}</div>
          )}

          {/* Success Message */}
          {success && (
            <div className="text-green-600 font-medium text-center">
              {success}
            </div>
          )}

          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 rounded-lg bg-blue-600 text-white font-semibold text-lg shadow-md hover:bg-blue-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? 'Processing...' : 'Submit'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FileConverter;
