import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [metadata, setMetadata] = useState({});
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setMetadata({
        name: selectedFile.name,
        size: `${(selectedFile.size / 1024).toFixed(2)} KB`,
        lastModified: new Date(selectedFile.lastModified).toLocaleString(),
      });
      setError('');
    } else {
      setFile(null);
      setMetadata({});
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      setFile(droppedFile);
      setMetadata({
        name: droppedFile.name,
        size: `${(droppedFile.size / 1024).toFixed(2)} KB`,
        lastModified: new Date(droppedFile.lastModified).toLocaleString(),
      });
      setError('');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a Word file to convert.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/convert', formData, {
        responseType: 'blob',
      });

      // Download the file
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${file.name.replace('.docx', '')}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
      setMetadata({});
    } catch (err) {
      setError('Error converting file. Please try again.');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 flex flex-col items-center">
      <div
        className="border-dashed border-2 border-gray-300 rounded-lg p-4 text-center w-full max-w-lg"
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        <h1 className="text-xl font-bold mb-2">Convert Word to PDF</h1>
        <p className="mb-4">Converting files made easier</p>
        <input
          type="file"
          accept=".docx"
          onChange={handleFileChange}
          className="mb-4"
        />
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          onClick={handleSubmit}
          disabled={loading}
        >
          {loading ? 'Converting...' : 'Convert to PDF'}
        </button>
        {error && <p className="text-red-500 mt-2">{error}</p>}
        <p className="mt-2">or drag and drop your Word file here</p>
      </div>
      {file && (
        <div className="bg-gray-100 mt-6 p-4 rounded shadow-md w-full max-w-lg">
          <h2 className="text-lg font-bold mb-2">File Details</h2>
          <p><strong>File Name:</strong> {metadata.name}</p>
          <p><strong>File Size:</strong> {metadata.size}</p>
          <p><strong>Last Modified:</strong> {metadata.lastModified}</p>
        </div>
      )}
    </div>
  );
};

export default FileConverter;
