import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [metadata, setMetadata] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);

  const handleFileChange = async (fileInput) => {
    if (
      fileInput &&
      fileInput.type ===
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
    ) {
      setFile(fileInput);
      setError('');
      try {
        const formData = new FormData();
        formData.append('file', fileInput);

        // Fetch metadata
        const response = await axios.post('http://localhost:5000/upload', formData);
        setMetadata(response.data);
      } catch (err) {
        setError('Error fetching file metadata.');
        console.error(err);
      }
    } else {
      setError('Please select a valid .docx file.');
      setFile(null);
      setMetadata(null);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange(droppedFile);
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFileChange(selectedFile);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a Word file to convert.');
      return;
    }

    setLoading(true);

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/convert', formData, {
        responseType: 'blob',
      });

      // Create a download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'converted.pdf');
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
      setMetadata(null); // Clear metadata after conversion
    } catch (err) {
      setError('Error converting file. Please try again.');
      console.error('Conversion error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col md:flex-row items-center justify-center min-h-screen p-4 bg-gray-100">
      {/* Left Section */}
      <div className="w-full md:w-2/3 max-w-lg p-6 bg-white rounded-lg shadow-md">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">Convert Word to PDF</h1>
        <p className="text-sm text-gray-500 text-center mb-6">Converting files made easier</p>
        <form onSubmit={handleSubmit} className="flex flex-col gap-4">
          {/* Drag-and-Drop Area */}
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`block w-full p-6 text-center border-2 border-dashed rounded-lg cursor-pointer focus:outline-none ${
              isDragOver ? 'bg-blue-100 border-blue-400' : 'bg-gray-50 border-gray-300'
            }`}
          >
            {file ? (
              <p className="text-gray-800">{file.name}</p>
            ) : (
              <p className="text-gray-500">Drag and drop your Word file here</p>
            )}
          </div>
          <p className="text-center text-gray-500">or</p>
          <label
            htmlFor="fileInput"
            className="block w-full p-3 text-center text-gray-700 bg-gray-200 rounded-lg cursor-pointer hover:bg-gray-300 focus:outline-none"
          >
            Select Word File
          </label>
          <input
            id="fileInput"
            type="file"
            accept=".docx"
            className="hidden"
            onChange={handleFileInput}
          />
          {error && <p className="text-sm text-red-500 text-center">{error}</p>}
          <button
            type="submit"
            onClick={handleSubmit}
            disabled={loading}
            className={`w-full p-3 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700 focus:outline-none ${
              loading && 'opacity-50 cursor-not-allowed'
            }`}
          >
            {loading ? 'Converting...' : 'Convert to PDF'}
          </button>
        </form>
      </div>

      {/* Right Section */}
      <div className="w-full md:w-1/3 max-w-md mt-6 md:mt-0 md:ml-6 p-6 bg-white rounded-lg shadow-md">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">File Details</h2>
        {metadata ? (
          <div className="text-sm text-gray-700">
            <p><strong>File Name:</strong> {metadata.fileName}</p>
            <p><strong>File Size:</strong> {metadata.size}</p>
            <p><strong>Date Created:</strong> {new Date(metadata.createdDate).toLocaleString()}</p>
            {metadata.authorName && <p><strong>Author:</strong> {metadata.authorName}</p>}
          </div>
        ) : (
          <p className="text-sm text-gray-500">No file selected.</p>
        )}
      </div>
    </div>
  );
};

export default FileConverter;
