import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [metadata, setMetadata] = useState({});

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/vnd.openxmlformats-officedocument.wordprocessingml.document') {
      setFile(selectedFile);
      setError('');
      setMetadata({
        name: selectedFile.name,
        size: `${(selectedFile.size / 1024).toFixed(2)} KB`,
        lastModified: new Date(selectedFile.lastModified).toLocaleString(),
      });
    } else {
      setError('Please select a valid .docx file');
      setFile(null);
      setMetadata({});
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a Word file to convert.');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/convert', formData, {
        responseType: 'blob',
      });

      // Create a download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `${file.name.replace('.docx', '')}.pdf`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
      setMetadata({});
    } catch (err) {
      setError('Error converting file. Please try again.');
      console.error('Conversion error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white shadow-md rounded-lg p-8 max-w-md w-full">
        <h1 className="text-3xl font-bold text-center mb-4">Convert Word to PDF</h1>
        <p className="text-center text-gray-600 mb-6">Converting files made easy</p>
        <form onSubmit={handleSubmit} className="space-y-4">
          <label className="block">
            <input
              type="file"
              accept=".docx"
              onChange={handleFileChange}
              className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            />
          </label>
          {error && <p className="text-red-500 text-sm">{error}</p>}
          <button
            type="submit"
            disabled={!file || loading}
            className={`w-full py-2 px-4 rounded ${
              !file || loading
                ? 'bg-gray-300 text-gray-700 cursor-not-allowed'
                : 'bg-blue-600 text-white hover:bg-blue-700'
            }`}
          >
            {loading ? 'Converting...' : 'Convert to PDF'}
          </button>
        </form>
        {file && (
          <div className="mt-6 bg-gray-100 p-4 rounded shadow">
            <h2 className="text-lg font-bold mb-2">File Details</h2>
            <p className="text-sm"><strong>File Name:</strong> {metadata.name}</p>
            <p className="text-sm"><strong>File Size:</strong> {metadata.size}</p>
            <p className="text-sm"><strong>Date Created:</strong> {metadata.lastModified}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default FileConverter;
