import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [metadata, setMetadata] = useState(null);
  const [isDragOver, setIsDragOver] = useState(false);
  const [password, setPassword] = useState('');

  const handleFileChange = async (fileInput) => {
    if (fileInput && fileInput.type === 'application/pdf') {
      setFile(fileInput);
      setError('');
    } else {
      setError('Please select a valid PDF file.');
      setFile(null);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
    setIsDragOver(true);
  };

  const handleDragLeave = () => {
    setIsDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setIsDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange(droppedFile);
  };

  const handleFileInput = (e) => {
    const selectedFile = e.target.files[0];
    handleFileChange(selectedFile);
  };

  const handlePasswordProtect = async (e) => {
    e.preventDefault();

    if (!file) {
      setError('Please select a PDF file to protect.');
      return;
    }

    if (!password) {
      setError('Please enter a password.');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('pdfFile', file);
    formData.append('password', password);

    try {
      const response = await axios.post('http://localhost:5001/upload', formData, {
        responseType: 'blob',
      });

      // Create a download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', `protected_${file.name}`);
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
      setPassword(''); // Clear password after protection
    } catch (err) {
      setError('Error protecting PDF. Please try again.');
      console.error('Protection error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-gray-100">
      {/* Section: Convert Word to PDF */}
      <div className="w-full md:w-2/3 max-w-lg p-6 bg-white rounded-lg shadow-md mb-8">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">Convert Word to PDF</h1>
        <p className="text-sm text-gray-500 text-center mb-6">Converting files made easier</p>
        {/* Existing Convert Word to PDF UI */}
        {/* ... */}
      </div>

      {/* Section: Protect Your PDF */}
      <div className="w-full md:w-2/3 max-w-lg p-6 bg-white rounded-lg shadow-md">
        <h1 className="text-2xl font-bold text-center text-gray-800 mb-4">Protect Your PDF</h1>
        <p className="text-sm text-gray-500 text-center mb-6">Password protect your PDFs with a click!</p>
        <form onSubmit={handlePasswordProtect} className="flex flex-col gap-4">
          {/* Drag-and-Drop Area */}
          <div
            onDragOver={handleDragOver}
            onDragLeave={handleDragLeave}
            onDrop={handleDrop}
            className={`block w-full p-6 text-center border-2 border-dashed rounded-lg cursor-pointer focus:outline-none ${
              isDragOver ? 'bg-blue-100 border-blue-400' : 'bg-gray-50 border-gray-300'
            }`}
          >
            {file ? (
              <p className="text-gray-800">{file.name}</p>
            ) : (
              <p className="text-gray-500">Drag and drop your PDF file here</p>
            )}
          </div>
          <p className="text-center text-gray-500">or</p>
          <label
            htmlFor="fileInput"
            className="block w-full p-3 text-center text-gray-700 bg-gray-200 rounded-lg cursor-pointer hover:bg-gray-300 focus:outline-none"
          >
            Select PDF File
          </label>
          <input
            id="fileInput"
            type="file"
            accept=".pdf"
            className="hidden"
            onChange={handleFileInput}
          />
          {error && <p className="text-sm text-red-500 text-center">{error}</p>}

          {/* Password Input */}
          <input
            type="password"
            placeholder="Enter password for PDF"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="p-3 w-full border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />

          {/* Button */}
          <button
            type="submit"
            disabled={loading}
            className={`w-full p-3 bg-blue-600 text-white rounded-lg shadow-md hover:bg-blue-700 focus:outline-none ${
              loading && 'opacity-50 cursor-not-allowed'
            }`}
          >
            {loading ? 'Encrypting...' : 'Download Encrypted PDF'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default FileConverter;
