import React, { useState } from 'react';
import axios from 'axios';

const FileConverter = () => {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (
      selectedFile &&
      selectedFile.type ===
        'application/vnd.openxmlformats-officedocument.wordprocessingml.document'
    ) {
      setFile(selectedFile);
      setError('');
    } else {
      setError('Please select a valid .docx file');
      setFile(null);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    handleFileChange({ target: { files: [droppedFile] } });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setLoading(true);
    setError('');

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:5000/convert', formData, {
        responseType: 'blob',
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'converted.pdf');
      document.body.appendChild(link);
      link.click();
      link.remove();
      window.URL.revokeObjectURL(url);

      setFile(null);
    } catch (err) {
      setError('Error converting file. Please try again.');
      console.error('Conversion error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex flex-col md:flex-row items-center justify-center min-h-screen bg-gray-100 px-6 py-10 space-y-10 md:space-y-0 md:space-x-8">
      {/* Main Content */}
      <div className="flex flex-col items-center text-center space-y-6 w-full md:w-2/3">
        <h1 className="text-4xl md:text-5xl font-extrabold text-gray-800">
          Convert Word to PDF
        </h1>
        <p className="text-lg text-gray-600">
          Effortlessly converting files made easier!
        </p>
        <form
          onSubmit={handleSubmit}
          className="flex flex-col items-center w-full border-2 border-dashed border-gray-400 rounded-lg p-6 bg-white shadow-md"
          onDragOver={(e) => e.preventDefault()}
          onDrop={handleDrop}
        >
          <label
            htmlFor="fileInput"
            className="bg-blue-500 text-white px-6 py-3 rounded-lg font-medium text-lg cursor-pointer hover:bg-blue-600 transition"
          >
            Select Word Files
          </label>
          <input
            id="fileInput"
            type="file"
            accept=".docx"
            onChange={handleFileChange}
            className="hidden"
          />
          <p className="mt-4 text-gray-500">
            or drag and drop your Word file here
          </p>
          {error && <p className="text-red-500 mt-4">{error}</p>}
          <button
            type="submit"
            disabled={!file || loading}
            className={`mt-6 px-6 py-3 rounded-lg font-medium text-lg ${
              loading ? 'bg-gray-400' : 'bg-green-500 hover:bg-green-600'
            } text-white transition`}
          >
            {loading ? 'Converting...' : 'Convert to PDF'}
          </button>
        </form>
      </div>

      {/* File Details Tab */}
      <div className="flex flex-col items-start bg-white rounded-lg shadow-lg p-6 w-full md:w-1/3">
        <h2 className="text-2xl font-bold text-gray-800 mb-4">File Details</h2>
        <p className="text-gray-600">File metadata will appear here.</p>
      </div>
    </div>
  );
};

export default FileConverter;
